// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "./StableVault.sol";
import "./interfaces/INativeStableVault.sol";

interface IWNative {
    function deposit() external payable;
    function withdraw(uint256 _amount) external;
}

contract NativeStableVault is StableVault, INativeStableVault {

    IWNative private native;

    constructor(address _stable, IWNative _native) StableVault(_stable) {
        native = _native;
    }

    function depositNative() public payable {
        native.deposit{value: msg.value}();
        IERC20Mintable(stable).mintFor(
            _msgSender(),
            msg.value
        );        
    }

    function withdrawNative(uint256 _amount) external returns (uint256) {
        IERC20Mintable(stable).burnFrom(_msgSender(), _amount);
        native.withdraw(_amount);
        payable(_msgSender()).transfer(
            _amount
        );
        return _amount;
    }

    receive() external payable {}
}

